mapper.select:
SELECT chall_no , chall_title , chall_content , chall_startAt , chall_closeAt , chall_state , main_img FROM chall WHERE chall_state = #{challVO.challState}
AND chall_title like '%' || #{challVO.challTitle} || '%'
ORDER BY chall_no DESC

SELECT COUNT(*)
FROM chall_like
WHERE chall_no = #{challNo}

SELECT COUNT(*)
FROM chall_like
WHERE chall_no = #{challNo}
AND user_no = #{userNo}

