mapper.insert:

	SELECT NVL(MAX(chall_no), 0) + 1
	FROM chall

INSERT INTO chall(
					chall_no
					, chall_title
					, chall_content
					, chall_startAt
        			, chall_closeAt
					, main_img
					, detail_img
					, chall_score

				)
VALUES 			(
