mapper.select:
SELECT count(*)
FROM chall
WHERE chall_state = #{challState}

mapper.insert:

	SELECT NVL(MAX(chall_no), 0) + 1
	FROM chall

INSERT INTO chall(
					chall_no
					, chall_title
					, chall_content
					, chall_startAt
        			, chall_closeAt
					, main_img
					, detail_img

				)
VALUES 			(
					#{challNo}
					, #{challTitle}
					, #{challContent}
					, #{challStartAt}
					, #{challCloseAt}
					, #{mainImg}
					, #{detailImg}
				)

mapper.update:
UPDATE chall
SET chall_title = #{challTitle}
	, chall_content = #{challContent}
	, chall_startAt = #{challStartAt}
	, chall_closeAt = #{challCloseAt}

	, main_img = #{mainImg}
	, detail_img = #{detailImg}


WHERE chall_no = #{challNo}
