mapper.select:
SELECT confirm_createAt
FROM chall_confirm
WHERE chall_no = #{challNo}
AND user_no = #{userNo}

SELECT TRUNC(chall_closeAt - SYSDATE) AS remaining_days
FROM chall
WHERE chall_no = #{challNo}

SELECT detail_img
FROM chall
WHERE chall_no = #{challNo}

mapper.insert:
INSERT INTO chall_enter(
						chall_enterAt
						, user_no
						, chall_no
						)
VALUES (
		SYSDATE
		, #{userNo}
		, #{challNo}
		)

mapper.select:
SELECT COUNT(*) 
FROM chall_enter
WHERE user_no = #{userNo}
AND chall_no = #{challNo}

mapper.insert:

	SELECT NVL(MAX(confirm_no), 0) + 1
	FROM chall_confirm

INSERT INTO chall_confirm (	
							confirm_no
							, confirm_content
							, confirm_createAt
							, user_no
							, chall_no
							)
VALUES (
		#{confirmNo}
		, #{confirmContent}
		, SYSDATE
		, #{userNo}
		, #{challNo}
		)
