mapper.select:
SELECT e.user_no, e.chall_no, COUNT(c.confirm_no) AS confirm_count, COUNT(c.confirm_no) * 10 AS score
FROM chall_enter e JOIN chall_confirm c ON e.user_no = c.user_no AND e.chall_no = c.chall_no WHERE e.chall_no = #{challNo} AND e.user_no = #{userNo}
GROUP BY e.user_no, e.chall_no

SELECT confirm_createAt
FROM chall_confirm
WHERE chall_no = #{challNo}
AND user_no = #{userNo}

SELECT TRUNC(chall_closeAt - SYSDATE) AS remaining_days
FROM chall
WHERE chall_no = #{challNo}

SELECT detail_img
FROM chall
WHERE chall_no = #{challNo}

mapper.insert:
INSERT INTO chall_enter(
						chall_enterAt
						, user_no
						, chall_no
						)
VALUES (
		SYSDATE
		, #{userNo}
		, #{challNo}
		)

mapper.select:
SELECT COUNT(*) 
FROM chall_enter
WHERE user_no = #{userNo}
AND chall_no = #{challNo}

mapper.insert:

	SELECT NVL(MAX(confirm_no), 0) + 1
	FROM chall_confirm

INSERT INTO chall_confirm (	
							confirm_no
							, confirm_content
							, confirm_createAt
							, user_no
							, chall_no
							)
VALUES (
		#{confirmNo}
		, #{confirmContent}
		, SYSDATE
		, #{userNo}
		, #{challNo}
		)

mapper.select:
SELECT user_no
FROM chall_confirm
WHERE confirm_no = #{confirmNo}

SELECT COUNT(*)
FROM chall_confirm
WHERE chall_no = #{challNo}
AND user_no = #{userNo}
AND TRUNC(confirm_createAt) = TRUNC(SYSDATE)

mapper.insert:

	SELECT NVL(MAX(confirm_reply_no), 0) + 1
	FROM chall_confirm_reply

INSERT INTO chall_confirm_reply(
								confirm_reply_no
								, confirm_reply_content
								, confirm_reply_createAt
								, user_no
								, confirm_no
								)
VALUES (
		#{confirmReplyNo}
		, #{confirmReplyContent}
		, sysdate
		, #{userNo}
		, #{confirmNo}
		)

mapper.select:
SELECT user_no
FROM chall_confirm_reply
WHERE confirm_reply_no = #{userNo}

SELECT COUNT(*)
FROM chall_confirm_reply
WHERE confirm_no = #{confirmNo}

mapper.insert:

	SELECT NVL(MAX(confirm_like_no), 0) + 1
	FROM chall_confirm_like

INSERT INTO chall_confirm_like(
								confirm_like_no
								, user_no
								, confirm_no
								)
VALUES (
		#{confirmLikeNo}
		, #{userNo}
		, #{confirmNo}
		)

mapper.delete:
DELETE FROM chall_confirm_like
WHERE confirm_no = #{confirmNo}
AND user_no = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM chall_confirm_like
WHERE confirm_no = #{confirmNo}
AND user_no = #{userNo}

SELECT COUNT(*)
FROM chall_confirm_like
WHERE confirm_no = #{confirmNo}

mapper.insert:

	SELECT NVL(MAX(confirm_declare_no), 0) + 1
	FROM chall_confirm_declare

INSERT INTO chall_confirm_declare(confirm_declare_no
                                    , confirm_declare_type
                                    , confirm_declareAt
                                    , confirm_user_no
                                    , confirm_no
                                )	
VALUES (
		#{confirmDeclareNo}
        , #{codeId}
        , SYSDATE
        , #{userNo}
        , #{confirmNo}
        )
