mapper.insert:

	SELECT NVL(MAX(msg_no), 0) + 1
	FROM msg

INSERT INTO msg
	(
		msg_no
		,msg_content
		,msg_type
		,msg_send_time
		,user_no
		,chat_no
	)
VALUES
	(
		#{msgNo}
		,#{msgContent}
		,#{msgType}
		,#{msgSendTime}
		,#{userNo}
		,#{chatNo}
	)

