mapper.select:
SELECT nickname
FROM users
WHERE user_no = #{userNo}

SELECT chat_type
FROM chat
WHERE chat_no = #{chatNo}

mapper.update:
UPDATE chat_entry
SET chat_user_state = 'Q2'
WHERE user_no = #{userNo} 
AND chat_no = #{chatNo}

mapper.select:
SELECT count(*)
FROM chat
WHERE chat_create_user = #{userNo}
AND chat_no = #{chatNo}
AND (chat_type = 'O2' OR chat_type = 'O3')

mapper.update:
UPDATE chat
SET chat_name = #{chatName}
WHERE chat_no = #{chatNo}

	UPDATE chat


            chat_image = #{chatImage},

