mapper.insert:

	SELECT NVL(MAX(file_no),1)+1
	FROM files

INSERT INTO files
(
	 file_no	
	 ,file_name
	 ,file_path
	 ,file_code
	 ,file_code_no )
VALUES
(
	#{fileNo}
	,#{fileName}
	,#{filePath}
	,#{fileCode}
	,#{fileCodeNo}
	)

mapper.select:
	SELECT *
    FROM (
        SELECT f.*
               , ROW_NUMBER() OVER (PARTITION BY f.file_code_no ORDER BY f.file_no ASC) AS rn
        FROM files f 
        JOIN chall_confirm c 
        ON f.file_code_no = c.confirm_no
        WHERE c.chall_no = #{challNo}
        AND c.user_no = #{userNo}
        AND f.file_code = #{fileCode}
    )
    WHERE rn = 1
    ORDER BY file_no desc
