mapper.insert:

	SELECT NVL(MAX(reply_no),0)+1
	FROM sns_reply

INSERT INTO sns_reply 
(
	 reply_no	
	 ,reply_content
	 ,reply_createAt
	 ,user_no
	 ,feed_no
	 )
VALUES
(
	#{replyNo}
	,#{replyContent}
	,#{replyCreateAt}
	,#{userNo}
	,#{feedNo}
	)


			SELECT NVL(MAX(sns_like_no),0)+1
			FROM sns_like

		Insert INTO sns_like(sns_like_no
                    		,user_no
                   			,feed_no) 
		VALUES(
				#{snsLikeNo}
         	   ,#{userNo}
         	   ,#{feedNo}
			)


			SELECT NVL(MAX(follow_no),0)+1
			FROM follow

		INSERT INTO follow(follow_no
							, follower_id
							, following_id )
		VALUES(
				#{followNo}
         	   ,#{userNo}
         	   ,#{followingId}
			)


			SELECT NVL(MAX(sns_block_no),0)+1
			FROM sns_block

		INSERT INTO sns_block(sns_block_no
							,block_user_no
							,blockat
							,user_no)
		VALUES(
				#{snsBlockNo}
         	   ,#{blockUserNo}
         	   ,sysdate
         	   ,#{userNo}
			)

mapper.select:
		SELECT follow_no 
          FROM follow 
         WHERE follower_id = #{userNo} 
         and following_id = #{otherNo}
